import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
 
export default function Edit() {
 const [form, setForm] = useState({
   item1: "",
   item2: "",
   item3: "",
   item4: "",
   item5: "",
   records: [],
 });
 const params = useParams();
 const navigate = useNavigate();
 
 useEffect(() => {
   async function fetchData() {
     const id = params.id.toString();
     const response = await fetch(`http://localhost:5000/record/${params.id.toString()}`);
 
     if (!response.ok) {
       const message = `An error has occurred: ${response.statusText}`;
       window.alert(message);
       return;
     }
 
     const record = await response.json();
     if (!record) {
       window.alert(`Record with id ${id} not found`);
       navigate("/");
       return;
     }
 
     setForm(record);
   }
 
   fetchData();
 
   return;
 }, [params.id, navigate]);
 
 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }
 
 async function onSubmit(e) {
   e.preventDefault();
   const editedPerson = {
     item1: form.item1,
     item2: form.item2,
     item3: form.item3,
     item4: form.item4,
   };
 
   // This will send a post request to update the data in the database.
   await fetch(`http://localhost:5000/update/${params.id}`, {
     method: "POST",
     body: JSON.stringify(editedPerson),
     headers: {
       'Content-Type': 'application/json'
     },
   });
 
   navigate("/");
 }
 
 // This following section will display the form that takes input from the user to update the data.
 return (
   <div>
     <h3>Update Record</h3>
     <br />
     <form onSubmit={onSubmit}>
        <div className="form-group">
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="item1Options"
             id="item1Almonds"
             value="Almonds"
             checked={form.item1 === "Almonds"}
             onChange={(e) => updateForm({ item1: e.target.value })}
           />
           <label htmlFor="item1Almonds" className="form-check-label">Almonds</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="item1Options"
             id="item1Honey"
             value="Honey"
             checked={form.item1 === "Honey"}
             onChange={(e) => updateForm({ item1: e.target.value })}
           />
           <label htmlFor="item1Honey" className="form-check-label">Honey</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="item1Options"
             id="item1Avocado"
             value="Avocado"
             checked={form.item1 === "Avocado"}
             onChange={(e) => updateForm({ item1: e.target.value })}
           />
           <label htmlFor="item1Senior" className="form-check-label">Avocado</label>
         </div>
       </div>
       <br />

       <div className="form-group">
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="item2Options"
             id="item2Almonds"
             value="Almonds"
             checked={form.item2 === "Almonds"}
             onChange={(e) => updateForm({ item2: e.target.value })}
           />
           <label htmlFor="item2Almonds" className="form-check-label">Almonds</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="item2Options"
             id="item2Honey"
             value="Honey"
             checked={form.item2 === "Honey"}
             onChange={(e) => updateForm({ item2: e.target.value })}
           />
           <label htmlFor="item2Honey" className="form-check-label">Honey</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="item2Options"
             id="item2Avocado"
             value="Avocado"
             checked={form.item2 === "Avocado"}
             onChange={(e) => updateForm({ item2: e.target.value })}
           />
           <label htmlFor="item2Senior" className="form-check-label">Avocado</label>
         </div>
       </div>
       <br />

       <div className="form-group">
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="item3Options"
             id="item3Almonds"
             value="Almonds"
             checked={form.item3 === "Almonds"}
             onChange={(e) => updateForm({ item3: e.target.value })}
           />
           <label htmlFor="item3Almonds" className="form-check-label">Almonds</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="item3Options"
             id="item3Honey"
